<?php	class short_dir extends MySQL_DB	{		function walkTree($1,$2,$3)	{			echo "\t<table border=\"1\" cellpadding=\"3\" cellspacing=\"0\" width=\"100%\">\n";			echo "\t\t<tr>\n";			echo "\t\t\t<td><b>Plant Name</b></td>\n";			echo "\t\t\t<td align=\"center\"><b>Pot Size</b></td>\n";			echo "\t\t\t<td align=\"center\"><b>Price</b></td>\n";			echo "\t\t\t<td align=\"center\"><b>Quantity</b></td>\n";			echo "\t\t</tr>\n";			query($this->1);			while ($this->result = mysql_fetch_object ($this->1)	{				echo "]\t\]t<tr>\n";				echo "\t\t\t<td colspan=\"4\"><h2>" . $this->result->plantCategory . "</h2></td>\n";				echo "\t\t</tr>\n";				$i = $this->result->catID;				query($this->2);				while ($this->result = mysql_fetch_object ($this->2)	{					echo "\t\t<tr>\n";					echo "\t\t\t<td colspan=\"4\"><h3>" . $this->result->plantGenus . "</h3></td>\n";					echo "\t\t</tr>\n";					$x = $this->result->genusID;					query($this->3);					while ($this->result = mysql_fetch_object ($this->3)	{						if ($this->result->varietalName == "\n")	{							$this->varName = "";						}	else	{							$this->varName = $this->result->varietalName . "&nbsp;";						}						if ($this->result->cultivar == "\n")	{							$this->cultName = "";						}	else	{							$this->cultName = "&#145;" . $this->result->cultivar . "&#146;";						}						echo "\t\t<tr>\n";						echo "\t\t\t<td width=\"75%\"><p><b><i>" . $this->varName . $this->cultName . "</b></p></td>\n";						echo "\t\t\t<td width=\"10%\"><p><b>" . $this->result->potSize . "</b></p></td>\n";						echo "\t\t\t<td width=\"10%\"><p><b>" . $this->result->price . "</b></p></td>\n";						echo "\t\t\t<td width=\"5%\">&nbsp;</td>\n";						echo "\t\t</tr>\n";					}					mysql_free_result ($this->3);				}				mysql_free_result ($this->2);			}			mysql_free_result ($this->1);			}	}?>