<?php	require ("../include/constants.php");	require ("../include/site_wide_variables.php");	require ("../include/header.inc.php");?>	<span class="sidebar"><img src="../images/1x1.gif" border="0" alt name="theImage" id="theImage" /></span> 	<div class="body">		<table border="1" cellpadding="1" cellspacing="0" width="559">			<tr>				<td class="header" style="text-align:left">Plant Name</td>				<td class="header">Pot Size</td>				<td class="header">Price</td>				<td class="header">Quantity</td>			</tr><?php	$db = mysql_connect (HOST, USER, PASS);	mysql_select_db ("plantage", $db);	$SQL1 = mysql_query ("SELECT * FROM PLANT_CAT");	while ($category = mysql_fetch_object ($SQL1))	{		echo "\t\t\t<tr>\n";		echo "\t\t\t\t<td colspan=\"4\" class=\"plantCategory\">" . $category->plantCategory . "</td>\n";		echo "\t\t\t</tr>\n";		$i = $category->catID;		$SQL2 = mysql_query ("SELECT * FROM PLANT_CAT AS c, PLANT_GEN AS g WHERE c.catID = g.catID AND c.catID = $i");		while ($genus = mysql_fetch_object ($SQL2))	{			echo "\t\t\t<tr>\n";			echo "\t\t\t\t<td colspan=\"4\" class=\"plantGenus\">" . $genus->plantGenus . "</td>\n";			echo "\t\t\t</tr>\n";			$x = $genus->genusID;			$SQL3 = mysql_query ("SELECT * FROM PLANT_CAT AS c, PLANT_GEN AS g, PLANT_PLANTS AS p, PLANT_POTS AS o, PLANT_PRICE AS r WHERE c.catID = g.catID AND c.catID = $i AND g.genusID = p.genusID AND g.genusID = $x AND p.potID = o.potID AND p.priceID = r.priceID");			while ($plant = mysql_fetch_object ($SQL3))	{				if ($plant->varietalName == "\n")	{					$varName = "";				}	else	{					$varName = $plant->varietalName . "&nbsp;";				}				if ($plant->cultivar == "\n")	{					$cultName = "";				}	else	{					$cultName = "&#145;" . $plant->cultivar . "&#146;";				}				echo "\t\t\t<tr>\n";				echo "\t\t\t\t<td width=\"65%\">&nbsp;&nbsp;<span class=\"cultivar\"><span class=\"varietal\">" . $varName . "</span>" . $cultName . "</span></td>\n";				echo "\t\t\t\t<td width=\"15%\" align=\"center\"><p class=\"potSize\">" . $plant->potSize . "</p></td>\n";				echo "\t\t\t\t<td width=\"*\" align=\"center\"><p class=\"price\">" . $plant->price . "</p></td>\n";				echo "\t\t\t\t<td width=\"5%\">&nbsp;</td>\n";				echo "\t\t\t</tr>\n";			}			mysql_free_result ($SQL3);		}		mysql_free_result ($SQL2);	}	mysql_free_result ($SQL1);	echo "\t\t</table>\n";	include ("include/footer.inc.php");?>	</div></body></html>
